import './App.css';
import React, { useState } from "react";
import validator from 'validator'

const App = () => {

const [errorMessage, setErrorMessage] = useState('')

const validate = (value) => {

	if (validator.isURL(value)) {
	setErrorMessage('Is Valid URL')
	} else {
	setErrorMessage('Is Not Valid URL')
	}
}

return (
	<div style={{
	marginLeft: '200px',
	}}>
	<pre>
  <h1 className='container'>ULR <span>Inputshortner</span></h1>
		<span>Enter URL: </span>
    <input className='textwidth' onChange={(e) => validate(e.target.value)}></input> <br /><br/>
    <input type="date" placeholder="Expiry Date" required/><br/><br/>
		<span style={{fontWeight: 'bold', color: 'red',}}>{errorMessage}</span>
	</pre>
	</div>
);
}

export default App
