  return (
    <>
    <div className="result">
      <p>{shortlink}</p>
      <button>Copy</button>

      {/* <CopyToClipboard text={shortlink} onCopy={()=> setCopied(true)}>
        <button className={copied ? "copied" : ""}>Copy</button>
      </CopyToClipboard> */}
    </div>

    <div className='inputContainer'>
        <h1>ULR <span>Inputshortner</span></h1>

            <div>
                <input type='text' placeholder='Paste your link'/>
                <button>Shortner</button>
            </div>

        {/* <p className='err'>{err}</p> */}
        </div>
    </>
  )
}

export default Shortnerresult;
