import React, { useState } from 'react';
import isURL from 'validator/lib/isURL';

const App = () => {
   const [val, setVal] = useState('');
   const [err, setErr] = useState('');

   const validate = (e) => {
      setVal(e.target.value);
      if (isURL(val)) {
         setErr('Valid URL');
      } else {
         setErr('Invalid URL');
      }
   };
   return (
<>

    <h1>ULR <span>Inputshortner</span></h1><br/>
        <div className='container'> 
        <input className='textwidth' type="text" placeholder="URL" value={val} onChange={validate} required/>
        <input type="date" placeholder="Expiry Date" required/> &nbsp;&nbsp;<br/>
        <button type="submit"> Submit</button><br/><br/>
        <p className='err'>{err}</p>

      </div>

    </>
   );
};
export default App;
