import React, { useState } from 'react';
import isURL from 'validator/lib/isURL';
import Shortnerresult from './Shortnerresult';

const App = () => {
   const [val, setVal] = useState('');
   const [err, setErr] = useState('');

   const validate = (e) => {
      setVal(e.target.value);
      if (isURL(val)) {
         setErr('Valid URL');
      } else {
         setErr('Invalid URL');
      }
   };
   return (
<>
    <form action="/action_page.php">

    <h1>ULR <span>Inputshortner</span></h1><br/>
        <div className='container'> 
        
        

      </div>

      <div class="flex-container">
  <div class="flex-item-left">
  <input className='textwidth' type="text" placeholder="URL" value={val} onChange={validate} required/>
        <input type="date" placeholder="Expiry Date" required/> &nbsp;&nbsp;
        <button type="submit"> Submit</button><br/><br/>
  </div>
  <div class="flex-item-right"><p className='err'>{err}</p></div>
</div>

    </form>
    <Shortnerresult/>
    </>
   );
};
export default App;
