import React, { useState } from 'react';
import isURL from 'validator/lib/isURL';

const App = () => {
   const [val, setVal] = useState('');
   const [err, setErr] = useState('');

   const validate = (e) => {
      setVal(e.target.value);
      if (isURL(val)) {
         setErr('Valid URL');
      } else {
         setErr('Invalid URL');
      }
   };
   return (
      <div>
        <h1>ULR <span>Inputshortner</span></h1><br/>
      <input className='textwidth' type="text" placeholder="URL" required/>&nbsp;&nbsp;

<input value={val} onChange={validate} />
         <p>{err}</p>

     <input type="date" placeholder="Expiry Date" required/> &nbsp;&nbsp;
      <button type="submit"> Submit</button>
         
      </div>
   );
};
export default App;
