import './App.css';
import React, { useState } from "react";
import validator from 'validator'

const App = () => {

const [errorMessage, setErrorMessage] = useState('')

const validate = (value) => {

	if (validator.isURL(value)) {
	setErrorMessage('URL IS Valid')
	} 
  else {
	setErrorMessage('Invalid URL or an expired shortened URL')

	}
}

return (
	<div className='url' style={{marginLeft: '200px',}}>
	<pre>
  <h1 className='container'>ULR <span>Inputshortner</span></h1>
		<span>Enter URL: </span>
    <input className='textwidth' onChange={(e) => validate(e.target.value)} required></input> <br /><br/>

    <span style={{fontWeight: 'bold',fontSize :'20px', color: 'red',}} > {errorMessage}</span><br/><br/>

    <span> Expiry Date: </span><input type="date" placeholder="Expiry Date" required/><br/><br/>

    <button type="submit"> Submit</button> &nbsp;&nbsp;
    <button type="submit"> History</button>

	</pre>
	</div>
);
}

export default App
