import React, { useState } from 'react';
import { Routes, Route, Link } from 'react-router-dom';

const App = () => {

   const [name, setName] = useState('');
   const [pwd, setPwd] = useState('');
   const [url, setUrl] = useState('');
   const [date, setDate] = useState('');

   const handle = () => {
      localStorage.setItem('Name', name);
      localStorage.setItem('Password', pwd);
      localStorage.setItem('Url', url);
      localStorage.setItem('Date', date);
   };
   const remove = () => {
      localStorage.removeItem('Name');
      localStorage.removeItem('Password');
      localStorage.removeItem('Url');
      localStorage.removeItem('Date');
   };
   return (
      <div className="inputContainer">
         <h1>URL <span>Inputshortner</span></h1>
         <input placeholder="Name" value={name} onChange={(e) => setName(e.target.value)}/>
       
         <input type="password" placeholder="Password" value={pwd} onChange={(e) => setPwd(e.target.value)}/>

         <input type="url" placeholder="url" value={pwd} onChange={(e) => setUrl(e.target.value)}/>

         <input type="date" placeholder="date" value={pwd} onChange={(e) => setDate(e.target.value)}/>

         <div>
            <button onClick={handle}>Done</button>
         </div>
         {localStorage.getItem('Name') && (
            <div>
               Name: <p>{localStorage.getItem('Name')}</p>
            </div>
         )}
         {localStorage.getItem('Password') && (
            <div>
               Password: <p>{localStorage.getItem('Password')}</p>
            </div>
         )}
         <div>
            <button onClick={remove}>Remove</button>
         </div>
      </div>
   );
};
export default App;
