import './App.css';
import React, { useState } from "react";

function App() {
  const [originalUrl, setOriginalUrl] = useState("");
  const [expiryDate, setExpiryDate] = useState("");
  const [shortenedUrls, setShortenedUrls] = useState([]);

  const handleGenerateUrl = () => {
    // Function to generate shortened URL
  };

  const handleCopyUrl = (index) => {
    // Function to copy shortened URL to clipboard
  };

  const handleEditExpiryDate = (index) => {
    // Function to edit expiry date of shortened URL
  };

  const handleDeleteUrl = (index) => {
    // Function to delete shortened URL from list
  };

  return (
    <div className="App">
      <h1>URL Shortener</h1>
      <div className="input-section">
        <label htmlFor="original-url">Original URL:</label>
        <input type="text" id="original-url" name="original-url" 
        value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)}/>

        <label htmlFor="expiry-date">Expiry Date:</label>
        <input type="date" id="expiry-date" name="expiry-date"
          value={expiryDate} onChange={(e) => setExpiryDate(e.target.value)}/>
        <button onClick={handleGenerateUrl}>Generate Shortened URL</button>
      </div>
      <div className="list-section">
        <table>
          <thead>
            <tr>
              <th>Original URL</th>
              <th>Shortened URL</th>
              <th>Expiry Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {shortenedUrls.map((url, index) => (
              <tr key={index}>
                <td>{url.originalUrl}</td>
                <td>
                  <a href={url.shortenedUrl} target="_blank" rel="noopener noreferrer">
                    {url.shortenedUrl}
                  </a>
                </td>
                <td>{url.expiryDate}</td>
                <td>
                  <button onClick={() => handleCopyUrl(index)}>Copy</button>
                  <button onClick={() => handleEditExpiryDate(index)}>Edit Expiry</button>
                  <button onClick={() => handleDeleteUrl(index)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
