import React, { useState } from 'react';
import isURL from 'validator/lib/isURL';

const App = (setInputValue) => {
   const [value,setValue] = useState("");
   const [val, setVal] = useState('');
   const [err, setErr] = useState('');

   const handleClick = () => {
      setInputValue(value);
      setValue("");
   }

   const validate = (e) => {
      setVal(e.target.value);
      if (isURL(val)) {
         setErr('Valid URL');
      } else {
         setErr('Invalid URL');
      }
   };
   return (
<>
    <form>
        <h1>ULR <span>Inputshortner</span></h1>
        <div className='container'>
        <input type="text" placeholder="URL" value={val||value} onChange={validate} required/>
       
    
        <input type="date" placeholder="Expiry Date" required/> &nbsp;&nbsp;
        <button> Submit</button><br/>
        <p className='err'>{err}</p>
        </div>
    </form>
    </>
   );
};
export default App;
