import React, { useState } from 'react';
import {CopyToClipboard} from 'react-copy-to-clipboard';

 const Shortnerresult = ({inputValue}) => {
    console.log(inputValue);
    const [shortlink, setShortLink] = useState("Hellow World");
    const [copied , setCopied] = useState(false);
  return (
    <>
    <div className="result">
      <p>{shortlink}</p>

      <CopyToClipboard text={shortlink} onCopy={()=> setCopied(true)}>
        <button className={copied ? "copied" : ""}>Copy</button>
      </CopyToClipboard>
    </div>
    </>
  )
}

export default Shortnerresult;
